import React, { useState, useEffect } from 'react';
import SimulationCard from '../components/SimulationCard';
import ResultModal from '../components/ResultModal';
import AwarenessVideo from '../components/AwarenessVideo';
import api from '../services/api';

const SimulationPage = ({ user }) => {
  const [simulation, setSimulation] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showResult, setShowResult] = useState(false);
  const [result, setResult] = useState(null);
  const [submitting, setSubmitting] = useState(false);
  const [showAwarenessVideo, setShowAwarenessVideo] = useState(false);
  const [awarenessScript, setAwarenessScript] = useState('');

  useEffect(() => {
    fetchSimulation();
  }, []);

  const fetchSimulation = async () => {
    try {
      const params = { user_id: user?.id };
      const data = await api.getPhishingSimulation(params);
      setSimulation(data);
    } catch (error) {
      console.error('Error fetching simulation:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAction = async (action) => {
    const clicked = action !== 'safe';
    const payload = {
      user_id: user?.id || 1,
      simulation_id: simulation?.id,
      clicked,
    };
    console.log('[SimulationPage] submitting interaction payload:', payload);
    setSubmitting(true);
    try {
      const res = await api.trackPhishingClick(payload);
      console.log('[SimulationPage] /api/phishing/click response:', res);
      console.log('[SimulationPage] clicked:', clicked, 'res.success:', res.success);

      // If user failed (unsafe action), get AI awareness script
      // Note: res.success means the API call succeeded, not that the user passed the simulation.
      // If clicked is true, the user failed (unsafe action).
      if (res.success && clicked) {
        console.log('[SimulationPage] User failed! Fetching AI awareness script...');
        try {
          const aiRes = await api.getAIAwarenessScript({
            user_id: user?.id,
            simulation_type: 'phishing',
            user_action: 'clicked_link',
            difficulty: simulation.difficulty
          });
          console.log('[SimulationPage] AI awareness response:', aiRes);
          if (aiRes && aiRes.script) {
            console.log('[SimulationPage] Setting awareness script and showing video');
            setAwarenessScript(aiRes.script);
            setShowAwarenessVideo(true);
            // Delay showing result modal until video is done
            return;
          } else {
            console.warn('[SimulationPage] No script in AI response');
          }
        } catch (e) {
          console.error("Failed to get AI awareness script", e);
        }
      } else {
        console.log('[SimulationPage] Skipping AI video. res.success:', res.success, 'clicked:', clicked);
      }

      setResult({
        isCorrect: res.success === true && !clicked,
        isSafe: !clicked,
        score: res.new_score,
        message: res.message,
      });
      setShowResult(true);
    } catch (error) {
      console.error('Error submitting interaction:', error);

      if (error.message && error.message.toLowerCase().includes('user not found')) {
        alert('Session expired or invalid user. Please log in again.');
        localStorage.removeItem('user');
        localStorage.removeItem('isLoggedIn');
        window.location.href = '/login';
        return;
      }

      setResult({
        isCorrect: false,
        isSafe: !clicked,
        score: null,
        message: (error && error.message) || 'Failed to submit interaction',
      });
      setShowResult(true);
      alert(`Error submitting interaction: ${error.message}`);
    } finally {
      setSubmitting(false);
    }
  };

  const handleCloseResult = () => {
    setShowResult(false);
    setResult(null);
    fetchSimulation(); // Load new simulation
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400 mx-auto mb-4"></div>
          <p className="text-white">Loading simulation...</p>
        </div>
      </div>
    );
  }

  if (!simulation) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="text-red-400 text-4xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-xl font-semibold text-white mb-2">Error Loading Simulation</h2>
          <p className="text-gray-300 mb-4">Unable to load the simulation. Please try again.</p>
          <button
            onClick={fetchSimulation}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold transition-all duration-200"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-white mb-4 font-cyber">
          Phishing Simulation
        </h1>
        <p className="text-gray-300 max-w-2xl mx-auto">
          You've received the following email. Based on your cybersecurity training,
          decide whether this is safe or unsafe to interact with.
        </p>
      </div>

      <SimulationCard
        simulation={simulation}
        onAction={handleAction}
        isSubmitting={submitting}
      />

      {/* DEBUG: Show state */}
      <div className="mt-4 p-4 bg-yellow-900 text-yellow-200 rounded text-sm">
        <strong>DEBUG:</strong> showAwarenessVideo = {showAwarenessVideo ? 'TRUE' : 'FALSE'},
        awarenessScript = {awarenessScript ? `"${awarenessScript.substring(0, 50)}..."` : 'EMPTY'}
      </div>

      <div className="mt-8 text-center">
        <div className="glassmorphism rounded-xl p-6 max-w-2xl mx-auto">
          <h3 className="text-lg font-semibold text-white mb-3">üí° Tips for Identifying Phishing</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-300">
            <div>
              <strong className="text-red-400">Red Flags:</strong>
              <ul className="mt-1 space-y-1">
                <li>‚Ä¢ Urgent language demanding immediate action</li>
                <li>‚Ä¢ Suspicious sender email addresses</li>
                <li>‚Ä¢ Requests for personal information</li>
                <li>‚Ä¢ Poor grammar and spelling</li>
              </ul>
            </div>
            <div>
              <strong className="text-green-400">Safe Practices:</strong>
              <ul className="mt-1 space-y-1">
                <li>‚Ä¢ Verify sender through official channels</li>
                <li>‚Ä¢ Hover over links before clicking</li>
                <li>‚Ä¢ Report suspicious emails to IT</li>
                <li>‚Ä¢ When in doubt, don't click</li>
              </ul>
            </div>
          </div>
        </div>
      </div>

      <ResultModal
        isOpen={showResult}
        onClose={handleCloseResult}
        result={result}
        type="simulation"
      />

      <AwarenessVideo
        isVisible={showAwarenessVideo}
        script={awarenessScript}
        onComplete={() => {
          setShowAwarenessVideo(false);
          // Show result modal after video
          setResult({
            isCorrect: false,
            isSafe: false,
            score: user?.score, // Score might be updated in background
            message: 'You fell for the phishing email!',
          });
          setShowResult(true);
        }}
      />
    </div>
  );
};

export default SimulationPage;
