        { id: 3, name: 'Emily Watson', department: 'HR', score: 230, avatar: 'üë©‚Äçüíº', streak: 10, badge: 'ü•â' },
        { id: 4, name: 'David Kim', department: 'Marketing', score: 220, avatar: 'üë®‚Äçüé®', streak: 8, badge: '‚≠ê' },
        { id: 5, name: 'Lisa Anderson', department: 'Sales', score: 215, avatar: 'üë©‚Äçüî¨', streak: 7, badge: '‚≠ê' },
        { id: 6, name: 'James Wilson', department: 'IT', score: 210, avatar: 'üë®‚Äçüíª', streak: 6, badge: '‚≠ê' },
        { id: 7, name: 'Maria Garcia', department: 'Operations', score: 205, avatar: 'üë©‚Äçüè´', streak: 5, badge: '‚≠ê' },
        { id: 8, name: 'Robert Taylor', department: 'Finance', score: 200, avatar: 'üë®‚Äç‚öïÔ∏è', streak: 4, badge: '‚≠ê' },
        { id: 9, name: 'Jennifer Lee', department: 'HR', score: 195, avatar: 'üë©‚ÄçüöÄ', streak: 3, badge: '‚≠ê' },
        { id: 10, name: 'William Brown', department: 'IT', score: 190, avatar: 'üë®‚Äçüîß', streak: 2, badge: '‚≠ê' }
      ];

      setTimeout(() => {
        setLeaderboardData(mockData);
        setLoading(false);
      }, 500);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
      setLoading(false);
    }
  };

  const getRankColor = (rank) => {
    if (rank === 1) return 'from-yellow-600 to-yellow-400';
    if (rank === 2) return 'from-gray-400 to-gray-300';
    if (rank === 3) return 'from-orange-600 to-orange-400';
    return 'from-blue-600 to-blue-400';
  };

  return (
    <div className="min-h-screen pb-12">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-white mb-4 font-cyber">
          üèÜ Leaderboard
        </h1>
        <p className="text-gray-300">
          See how you rank against your colleagues in cybersecurity awareness
        </p>
      </div>

      {/* Timeframe Selector */}
      <div className="flex justify-center mb-8">
        <div className="glassmorphism rounded-xl p-2 inline-flex space-x-2">
          {['week', 'month', 'all-time'].map((tf) => (
            <button
              key={tf}
              onClick={() => setTimeframe(tf)}
              className={`px-6 py-2 rounded-lg font-semibold transition-all duration-200 ${timeframe === tf
                  ? 'bg-blue-600 text-white'
                  : 'text-gray-400 hover:text-white'
                }`}
            >
              {tf === 'all-time' ? 'All Time' : tf.charAt(0).toUpperCase() + tf.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Top 3 Podium */}
      <div className="max-w-4xl mx-auto mb-12">
        <div className="grid grid-cols-3 gap-4 items-end">
          {/* 2nd Place */}
          {leaderboardData[1] && (
            <div className="text-center">
              <div className="glassmorphism rounded-xl p-6 bg-gradient-to-br from-gray-600/30 to-gray-400/30 border-2 border-gray-400">
                <div className="text-6xl mb-3">{leaderboardData[1].avatar}</div>
                <div className="text-4xl mb-2">ü•à</div>
                <div className="text-xl font-bold text-white mb-1">{leaderboardData[1].name}</div>
                <div className="text-sm text-gray-400 mb-2">{leaderboardData[1].department}</div>
                <div className="text-3xl font-bold text-gray-300">{leaderboardData[1].score}</div>
                <div className="text-xs text-gray-400">points</div>
              </div>
            </div>
          )}

          {/* 1st Place */}
          {leaderboardData[0] && (
            <div className="text-center">
              <div className="glassmorphism rounded-xl p-8 bg-gradient-to-br from-yellow-600/30 to-yellow-400/30 border-2 border-yellow-400 transform scale-110">
                <div className="text-7xl mb-3">{leaderboardData[0].avatar}</div>
                <div className="text-5xl mb-2 animate-bounce">üëë</div>
                <div className="text-2xl font-bold text-white mb-1">{leaderboardData[0].name}</div>
                <div className="text-sm text-gray-300 mb-2">{leaderboardData[0].department}</div>
                <div className="text-4xl font-bold text-yellow-400">{leaderboardData[0].score}</div>
                <div className="text-xs text-gray-300">points</div>
                <div className="mt-3 text-orange-400 flex items-center justify-center">
                  <span className="text-2xl mr-1">üî•</span>
                  <span className="font-bold">{leaderboardData[0].streak} day streak</span>
                </div>
              </div>
            </div>
          )}

          {/* 3rd Place */}
          {leaderboardData[2] && (
            <div className="text-center">
              <div className="glassmorphism rounded-xl p-6 bg-gradient-to-br from-orange-600/30 to-orange-400/30 border-2 border-orange-400">
                <div className="text-6xl mb-3">{leaderboardData[2].avatar}</div>
                <div className="text-4xl mb-2">ü•â</div>
                <div className="text-xl font-bold text-white mb-1">{leaderboardData[2].name}</div>
                <div className="text-sm text-gray-400 mb-2">{leaderboardData[2].department}</div>
                <div className="text-3xl font-bold text-orange-300">{leaderboardData[2].score}</div>
                <div className="text-xs text-gray-400">points</div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Full Leaderboard */}
      <div className="max-w-4xl mx-auto">
        <div className="glassmorphism rounded-xl p-6">
          <h2 className="text-2xl font-bold text-white mb-6">Full Rankings</h2>

          {loading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400 mx-auto mb-4"></div>
              <p className="text-gray-400">Loading leaderboard...</p>
            </div>
          ) : (
            <div className="space-y-3">
              {leaderboardData.map((user, index) => (
                <div
                  key={user.id}
                  className={`flex items-center p-4 rounded-lg transition-all duration-200 hover:scale-102 ${index < 3
                      ? 'bg-gradient-to-r ' + getRankColor(index + 1) + ' bg-opacity-20'
                      : 'bg-gray-800/50 hover:bg-gray-800'
                    }`}
                >
                  {/* Rank */}
                  <div className="w-12 text-center">
                    <span className="text-2xl font-bold text-white">#{index + 1}</span>
                  </div>

                  {/* Badge */}
                  <div className="text-3xl mx-3">{user.badge}</div>

                  {/* Avatar */}
                  <div className="text-4xl mr-4">{user.avatar}</div>

                  {/* User Info */}
                  <div className="flex-1">
                    <div className="text-lg font-semibold text-white">{user.name}</div>
                    <div className="text-sm text-gray-400">{user.department}</div>
                  </div>

                  {/* Streak */}
                  <div className="text-center mr-6">
                    <div className="text-orange-400 flex items-center">
                      <span className="text-xl mr-1">üî•</span>
                      <span className="font-bold">{user.streak}</span>
                    </div>
                    <div className="text-xs text-gray-400">streak</div>
                  </div>

                  {/* Score */}
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-400">{user.score}</div>
                    <div className="text-xs text-gray-400">points</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Department Rankings */}
      <div className="max-w-4xl mx-auto mt-8">
        <div className="glassmorphism rounded-xl p-6">
          <h2 className="text-2xl font-bold text-white mb-6">üè¢ Department Rankings</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {['IT', 'Finance', 'HR', 'Marketing', 'Sales', 'Operations'].map((dept, index) => {
              const deptScore = 800 - index * 50;
              const deptUsers = leaderboardData.filter(u => u.department === dept).length;
              return (
                <div key={dept} className="bg-gray-800/50 rounded-lg p-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="text-lg font-semibold text-white">{dept}</div>
                      <div className="text-sm text-gray-400">{deptUsers} members</div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-blue-400">{deptScore}</div>
                      <div className="text-xs text-gray-400">avg score</div>
                    </div>
                  </div>
                  <div className="mt-3 w-full bg-gray-700 rounded-full h-2">
                    <div
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${(deptScore / 1000) * 100}%` }}
                    ></div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
