// Minimal API service using fetch and the existing frontend proxy
const handleResponse = async (res) => {
  const contentType = res.headers.get('content-type') || '';
  if (!res.ok) {
    const text = await res.text();
    throw new Error(text || `HTTP ${res.status}`);
  }
  if (contentType.includes('application/json')) return res.json();
  if (contentType.includes('application/pdf') || contentType.includes('application/octet-stream')) return res.blob();
  return res.text();
};

export const getSimulations = () => fetch('/api/simulations').then(handleResponse);
export const getRandomSimulation = () => fetch('/api/simulate_attack').then(handleResponse);
export const submitSimulation = (payload) => fetch('/api/submit_interaction', {
  method: 'POST', headers: {'Content-Type': 'application/json'}, body: JSON.stringify(payload)
}).then(handleResponse);

export const coachAdvice = (payload) => fetch('/api/coach', {method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify(payload)}).then(handleResponse);

export const getBehaviorAnalytics = () => fetch('/api/analytics/behavior').then(handleResponse);

export const requestCertificate = (payload) => fetch('/api/certificate', {method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify(payload)}).then(handleResponse);

export default { getSimulations, getRandomSimulation, submitSimulation, coachAdvice, getBehaviorAnalytics, requestCertificate };
