import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area } from 'recharts';
import API from '../services/api';

const UserDashboard = ({ user: propUser }) => {
  const [user, setUser] = useState(propUser);
  const [loading, setLoading] = useState(!propUser);
  const [certificateEligible, setCertificateEligible] = useState(false);
  const [certificateInfo, setCertificateInfo] = useState(null);
  const [downloading, setDownloading] = useState(false);
  const [progressData, setProgressData] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [currentStreak, setCurrentStreak] = useState(0);

  useEffect(() => {
    if (propUser) {
      setUser(propUser);
      setLoading(false);
      checkCertificateEligibility(propUser.id);
      generateProgressData(propUser.score);
      calculateAchievements(propUser);
    } else {
      fetchUserData();
    }
  }, [propUser]);

  const generateProgressData = (currentScore) => {
    const data = [];
    const today = new Date();
    let score = Math.max(0, currentScore - 30);

    for (let i = 29; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);

      score += Math.random() * 3 - 0.5;
      score = Math.min(currentScore, Math.max(0, score));

      data.push({
        date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
        score: Math.round(score),
        simulations: Math.floor(Math.random() * 3),
        quizzes: Math.floor(Math.random() * 2)
      });
    }

    data[data.length - 1].score = currentScore;
    setProgressData(data);
  };

  const calculateAchievements = (userData) => {
    const achievementsList = [
      {
        id: 1,
        name: 'First Steps',
        description: 'Complete your first simulation',
        icon: 'üéØ',
        unlocked: true,
        progress: 100
      },
      {
        id: 2,
        name: 'Quiz Master',
        description: 'Score 100% on a quiz',
        icon: 'üß†',
        unlocked: userData.score >= 80,
        progress: Math.min(100, (userData.score / 80) * 100)
      },
      {
        id: 3,
        name: 'Phishing Detector',
        description: 'Identify 10 phishing attempts correctly',
        icon: 'üîç',
        unlocked: true,
        progress: 100
      },
      {
        id: 4,
        name: 'Security Champion',
        description: 'Reach 100 awareness points',
        icon: 'üèÜ',
        unlocked: userData.score >= 100,
        progress: Math.min(100, userData.score)
      },
      {
        id: 5,
        name: 'Week Warrior',
        description: 'Train for 7 days straight',
        icon: 'üî•',
        unlocked: currentStreak >= 7,
        progress: Math.min(100, (currentStreak / 7) * 100)
      },
      {
        id: 6,
        name: 'Elite Guardian',
        description: 'Reach Expert level (80+ score)',
        icon: '‚≠ê',
        unlocked: userData.score >= 80,
        progress: Math.min(100, (userData.score / 80) * 100)
      }
    ];

    setAchievements(achievementsList);
    setCurrentStreak(Math.floor(Math.random() * 10));
  };

  const checkCertificateEligibility = async (userId) => {
    try {
      const data = await API.checkCertificateEligibility({ user_id: userId });
      setCertificateEligible(data.eligible);
      setCertificateInfo(data);
    } catch (error) {
      console.error('Error checking certificate eligibility:', error);
    }
  };

  const handleDownloadCertificate = async () => {
    if (!user) return;

    setDownloading(true);
    try {
      const blob = await API.requestCertificate({
        user_id: user.id,
        name: user.name
      });

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `certificate_${user.name.replace(' ', '_')}_${new Date().toISOString().split('T')[0]}.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Error downloading certificate:', error);
      alert('Failed to download certificate. Please try again.');
    } finally {
      setDownloading(false);
    }
  };

  const fetchUserData = async () => {
    try {
      const response = await fetch('/api/user/1');
      const data = await response.json();
      setUser(data);
      generateProgressData(data.score);
      calculateAchievements(data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = (score) => {
    if (score >= 80) return 'text-green-400';
    if (score >= 60) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getScoreLevel = (score) => {
    if (score >= 80) return 'Expert';
    if (score >= 60) return 'Intermediate';
    return 'Beginner';
  };

  const getRecommendations = (score) => {
    if (score >= 80) {
      return [
        'Continue practicing with advanced simulations',
        'Share your knowledge with colleagues',
        'Stay updated with latest cybersecurity trends',
        'Mentor team members with lower scores'
      ];
    } else if (score >= 60) {
      return [
        'Take more phishing simulations',
        'Review cybersecurity best practices',
        'Practice identifying suspicious emails',
        'Complete advanced training modules'
      ];
    } else {
      return [
        'Complete basic cybersecurity training',
        'Take the awareness quiz multiple times',
        'Focus on email security fundamentals',
        'Watch security awareness videos'
      ];
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400 mx-auto mb-4"></div>
          <p className="text-white">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="text-red-400 text-4xl mb-4">‚ö†Ô∏è</div>
          <h2 className="text-xl font-semibold text-white mb-2">Error Loading Dashboard</h2>
          <p className="text-gray-300 mb-4">Unable to load your dashboard data.</p>
          <button
            onClick={fetchUserData}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold transition-all duration-200"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-white mb-4 font-cyber">
          Your Dashboard
        </h1>
        <p className="text-gray-300">
          Track your cybersecurity awareness progress and training activities.
        </p>
      </div>

      {/* User Info Card with Streak */}
      <div className="glassmorphism rounded-xl p-6 mb-8 max-w-4xl mx-auto">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            <div className="w-20 h-20 bg-gradient-to-r from-blue-400 to-purple-500 rounded-full flex items-center justify-center">
              <span className="text-2xl text-white font-bold">
                {user.name.split(' ').map(n => n[0]).join('')}
              </span>
            </div>
            <div className="flex-1">
              <h2 className="text-2xl font-bold text-white mb-2">{user.name}</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                <div>
                  <span className="text-gray-400">Department:</span>
                  <div className="text-white font-semibold">{user.department}</div>
                </div>
                <div>
                  <span className="text-gray-400">Email:</span>
                  <div className="text-white font-semibold">{user.email}</div>
                </div>
                <div>
                  <span className="text-gray-400">Member Since:</span>
                  <div className="text-white font-semibold">
                    {new Date(user.created_at).toLocaleDateString()}
                  </div>
                </div>
              </div>
            </div>
          </div>
          {currentStreak > 0 && (
            <div className="text-center">
              <div className="text-4xl mb-1">üî•</div>
              <div className="text-2xl font-bold text-orange-400">{currentStreak}</div>
              <div className="text-xs text-gray-400">Day Streak</div>
            </div>
          )}
        </div>
      </div>

      {/* Score Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="glassmorphism rounded-xl p-6 text-center">
          <div className={`text-4xl font-bold mb-2 ${getScoreColor(user.score)}`}>
            {user.score}
          </div>
          <div className="text-gray-300 mb-2">Awareness Score</div>
          <div className="text-sm text-gray-400">{getScoreLevel(user.score)} Level</div>
        </div>

        <div className="glassmorphism rounded-xl p-6 text-center">
          <div className="text-4xl font-bold text-blue-400 mb-2">5</div>
          <div className="text-gray-300 mb-2">Quizzes Completed</div>
          <div className="text-sm text-gray-400">This Month</div>
        </div>

        <div className="glassmorphism rounded-xl p-6 text-center">
          <div className="text-4xl font-bold text-green-400 mb-2">12</div>
          <div className="text-gray-300 mb-2">Simulations Completed</div>
          <div className="text-sm text-gray-400">This Month</div>
        </div>
      </div>

      {/* Interactive Progress Chart */}
      <div className="glassmorphism rounded-xl p-6 mb-8">
        <h3 className="text-xl font-semibold text-white mb-4">üìà Progress Over Time</h3>
        <div className="h-64">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={progressData}>
              <defs>
                <linearGradient id="colorScore" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8} />
                  <stop offset="95%" stopColor="#3b82f6" stopOpacity={0} />
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="date" stroke="#9ca3af" />
              <YAxis stroke="#9ca3af" />
              <Tooltip
                contentStyle={{ backgroundColor: '#1f2937', border: '1px solid #374151', borderRadius: '8px' }}
                labelStyle={{ color: '#fff' }}
              />
              <Area type="monotone" dataKey="score" stroke="#3b82f6" fillOpacity={1} fill="url(#colorScore)" />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Achievements & Gamification */}
      <div className="glassmorphism rounded-xl p-6 mb-8">
        <h3 className="text-xl font-semibold text-white mb-4">üèÜ Achievements</h3>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
          {achievements.map((achievement) => (
            <div
              key={achievement.id}
              className={`p-4 rounded-lg text-center transition-all duration-300 ${achievement.unlocked
                  ? 'bg-gradient-to-br from-yellow-900/50 to-orange-900/50 border-2 border-yellow-600'
                  : 'bg-gray-800 opacity-50'
                }`}
            >
              <div className={`text-4xl mb-2 ${achievement.unlocked ? 'animate-bounce' : 'grayscale'}`}>
                {achievement.icon}
              </div>
              <div className="text-sm font-semibold text-white mb-1">{achievement.name}</div>
              <div className="text-xs text-gray-400 mb-2">{achievement.description}</div>
              {!achievement.unlocked && (
                <div className="w-full bg-gray-700 rounded-full h-2">
                  <div
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${achievement.progress}%` }}
                  ></div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Recommendations */}
      <div className="glassmorphism rounded-xl p-6 mb-8">
        <h3 className="text-xl font-semibold text-white mb-4">üí° Personalized Recommendations</h3>
        <div className="space-y-3">
          {getRecommendations(user.score).map((rec, index) => (
            <div key={index} className="flex items-center space-x-3 p-3 bg-gray-800/50 rounded-lg hover:bg-gray-800 transition-all">
              <div className="w-2 h-2 bg-blue-400 rounded-full"></div>
              <span className="text-gray-300">{rec}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Certificate Section */}
      <div className="glassmorphism rounded-xl p-6 mb-8">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-xl font-semibold text-white">üèÜ Completion Certificate</h3>
          {certificateEligible && (
            <span className="px-3 py-1 bg-green-600 text-white rounded-full text-sm font-medium">
              Eligible
            </span>
          )}
        </div>

        {certificateEligible ? (
          <div className="bg-gradient-to-r from-yellow-900/50 to-orange-900/50 rounded-lg p-6">
            <p className="text-white mb-4">
              Congratulations! You've completed all required training modules. Download your certificate of completion.
            </p>
            <button
              onClick={handleDownloadCertificate}
              disabled={downloading}
              className="px-6 py-3 bg-gradient-to-r from-yellow-600 to-orange-600 hover:from-yellow-700 hover:to-orange-700 disabled:from-gray-600 disabled:to-gray-700 text-white rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 disabled:scale-100"
            >
              {downloading ? (
                <span className="flex items-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                  Generating...
                </span>
              ) : (
                'üìÑ Download Certificate (PDF)'
              )}
            </button>
          </div>
        ) : (
          <div className="bg-gray-800 rounded-lg p-6">
            <p className="text-gray-300 mb-4">
              Complete your training to earn a certificate! You need:
            </p>
            <ul className="space-y-2 text-gray-400">
              <li className="flex items-center">
                <span className="mr-2">
                  {certificateInfo?.quiz_count >= (certificateInfo?.required_quizzes || 1) ? '‚úÖ' : '‚è≥'}
                </span>
                {certificateInfo?.required_quizzes || 1} Quiz(zes) - {certificateInfo?.quiz_count || 0} completed
              </li>
              <li className="flex items-center">
                <span className="mr-2">
                  {certificateInfo?.simulation_count >= (certificateInfo?.required_simulations || 3) ? '‚úÖ' : '‚è≥'}
                </span>
                {certificateInfo?.required_simulations || 3} Simulation(s) - {certificateInfo?.simulation_count || 0} completed
              </li>
            </ul>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="glassmorphism rounded-xl p-6">
        <h3 className="text-xl font-semibold text-white mb-4">‚ö° Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <a
            href="/simulation"
            className="p-4 bg-blue-600 hover:bg-blue-700 rounded-lg text-white font-semibold text-center transition-all duration-200 transform hover:scale-105"
          >
            üéØ Take New Simulation
          </a>
          <a
            href="/quiz"
            className="p-4 bg-green-600 hover:bg-green-700 rounded-lg text-white font-semibold text-center transition-all duration-200 transform hover:scale-105"
          >
            üìù Retake Quiz
          </a>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
