import React, { useEffect, useState, useRef } from 'react';

const AwarenessVideo = ({ script, onComplete, isVisible }) => {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [displayedText, setDisplayedText] = useState('');
  const [animationPhase, setAnimationPhase] = useState(0); // 0: intro, 1: warning, 2: tips
  const speechRef = useRef(null);

  useEffect(() => {
    if (isVisible && script) {
      startVideo();
    }
  }, [isVisible, script]);

  const startVideo = () => {
    if (!window.speechSynthesis) {
      console.error("Web Speech API not supported");
      onComplete();
      return;
    }

    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(script);
    utterance.rate = 0.95;
    utterance.pitch = 1.0;
    utterance.volume = 1.0;

    const voices = window.speechSynthesis.getVoices();
    const preferredVoice = voices.find(v => v.name.includes('Google US English') || v.name.includes('Microsoft Zira') || v.lang === 'en-US');
    if (preferredVoice) utterance.voice = preferredVoice;

    utterance.onstart = () => {
      setIsSpeaking(true);
      typeWriterEffect(script);
      animatePhases();
    };

    utterance.onend = () => {
      setIsSpeaking(false);
      setTimeout(onComplete, 2000);
    };

    utterance.onerror = (e) => {
      console.error("Speech synthesis error", e);
      setIsSpeaking(false);
      onComplete();
    };

    speechRef.current = utterance;
    setTimeout(() => window.speechSynthesis.speak(utterance), 100);
  };

  const animatePhases = () => {
    // Change animation phases during the video
    setTimeout(() => setAnimationPhase(1), 3000);  // Warning phase
    setTimeout(() => setAnimationPhase(2), 8000);  // Tips phase
  };

  const stopVideo = () => {
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    setIsSpeaking(false);
    setDisplayedText('');
  };

  const typeWriterEffect = (text) => {
    let i = 0;
    setDisplayedText('');
    const interval = setInterval(() => {
      setDisplayedText((prev) => prev + text.charAt(i));
      i++;
      if (i >= text.length) clearInterval(interval);
    }, 40);
  };

  const shouldShow = isVisible || isSpeaking || displayedText.length > 0;

  if (!shouldShow) return null;

  return (
    <div
      className="fixed inset-0 flex flex-col items-center justify-center p-4"
      style={{
        background: 'linear-gradient(135deg, #0f172a 0%, #1e1b4b 50%, #0f172a 100%)',
        zIndex: 9999,
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0
      }}
    >
      {/* Animated Background Particles */}
      <div className="absolute inset-0 overflow-hidden opacity-30">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute bg-cyan-400 rounded-full animate-float"
            style={{
              width: Math.random() * 4 + 2 + 'px',
              height: Math.random() * 4 + 2 + 'px',
              left: Math.random() * 100 + '%',
              top: Math.random() * 100 + '%',
              animationDelay: Math.random() * 5 + 's',
              animationDuration: Math.random() * 10 + 10 + 's'
            }}
          />
        ))}
      </div>

      {/* Main Animation Scene */}
      <div className="relative mb-8 w-full max-w-2xl h-64 flex items-center justify-center">
        {/* Phase 0: Alert Icon Animation */}
        {animationPhase === 0 && (
          <div className="animate-scale-pulse">
            <svg width="200" height="200" viewBox="0 0 200 200" className="animate-spin-slow">
              <circle cx="100" cy="100" r="80" fill="none" stroke="#ef4444" strokeWidth="4" strokeDasharray="10 5" />
              <circle cx="100" cy="100" r="60" fill="none" stroke="#f59e0b" strokeWidth="4" strokeDasharray="5 3" />
              <path d="M100 60 L100 120 M100 140 L100 150" stroke="#fbbf24" strokeWidth="8" strokeLinecap="round" className="animate-pulse" />
            </svg>
          </div>
        )}

        {/* Phase 1: Hacker/Threat Animation */}
        {animationPhase === 1 && (
          <div className="relative">
            {/* Computer Screen */}
            <div className="w-64 h-48 bg-gray-900 rounded-lg border-4 border-gray-700 p-4 animate-shake">
              <div className="w-full h-full bg-black rounded flex flex-col items-center justify-center">
                <div className="text-red-500 text-4xl animate-pulse">‚ö†Ô∏è</div>
                <div className="text-red-400 text-xs mt-2 font-mono animate-blink">PHISHING DETECTED</div>
                {/* Animated Code Lines */}
                <div className="mt-4 w-full space-y-1">
                  {[...Array(4)].map((_, i) => (
                    <div key={i} className="h-1 bg-red-500 rounded animate-slide-right" style={{ animationDelay: i * 0.2 + 's', width: Math.random() * 60 + 40 + '%' }}></div>
                  ))}
                </div>
              </div>
            </div>
            {/* Danger Symbols */}
            <div className="absolute -top-4 -right-4 text-6xl animate-bounce">üö®</div>
            <div className="absolute -bottom-4 -left-4 text-5xl animate-spin-slow">üîí</div>
          </div>
        )}

        {/* Phase 2: Shield/Protection Animation */}
        {animationPhase === 2 && (
          <div className="relative animate-float">
            {/* Shield */}
            <svg width="200" height="200" viewBox="0 0 200 200">
              <defs>
                <linearGradient id="shieldGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                  <stop offset="0%" style={{ stopColor: '#22d3ee', stopOpacity: 1 }} />
                  <stop offset="100%" style={{ stopColor: '#3b82f6', stopOpacity: 1 }} />
                </linearGradient>
              </defs>
              <path
                d="M100 20 L160 50 L160 110 Q160 160 100 180 Q40 160 40 110 L40 50 Z"
                fill="url(#shieldGradient)"
                stroke="#60a5fa"
                strokeWidth="3"
                className="animate-pulse"
              />
              <text x="100" y="110" fontSize="60" fill="white" textAnchor="middle" className="animate-pulse">‚úì</text>
            </svg>
            {/* Sparkles */}
            {[...Array(8)].map((_, i) => (
              <div
                key={i}
                className="absolute text-2xl animate-ping"
                style={{
                  top: Math.sin(i * Math.PI / 4) * 100 + 100 + 'px',
                  left: Math.cos(i * Math.PI / 4) * 100 + 100 + 'px',
                  animationDelay: i * 0.3 + 's'
                }}
              >‚ú®</div>
            ))}
          </div>
        )}
      </div>

      {/* Title */}
      <h2 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-500 mb-6 animate-fade-in">
        ü§ñ AI Security Awareness
      </h2>

      {/* Captions with Background */}
      <div
        className="max-w-3xl w-full p-8 rounded-2xl border-2 min-h-[180px] flex items-center justify-center backdrop-blur-sm animate-slide-up"
        style={{
          backgroundColor: 'rgba(17, 24, 39, 0.8)',
          borderColor: '#374151',
          boxShadow: '0 0 30px rgba(34, 211, 238, 0.3)'
        }}
      >
        <p className="text-xl text-cyan-300 font-mono text-center leading-relaxed">
          {displayedText}
          <span className="animate-pulse">|</span>
        </p>
      </div>

      {/* Progress indicator */}
      {isSpeaking && (
        <div className="mt-6 flex items-center gap-3">
          <div className="w-3 h-3 bg-cyan-400 rounded-full animate-bounce"></div>
          <div className="w-3 h-3 bg-blue-400 rounded-full animate-bounce" style={{ animationDelay: '0.15s' }}></div>
          <div className="w-3 h-3 bg-cyan-400 rounded-full animate-bounce" style={{ animationDelay: '0.3s' }}></div>
        </div>
      )}

      <button
        onClick={() => { stopVideo(); onComplete(); }}
        className="mt-8 px-8 py-3 rounded-full text-base font-semibold transition-all hover:scale-110 hover:shadow-lg"
        style={{
          background: 'linear-gradient(135deg, #374151 0%, #1f2937 100%)',
