import React, { useEffect, useState } from 'react';

const Leaderboard = () => {
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    // For now read users from localStorage or show placeholder
    const u = JSON.parse(localStorage.getItem('leaderboard') || '[]');
    setLeaders(u.length ? u : [
      { name: 'Alice', score: 95 },
      { name: 'Bob', score: 88 },
      { name: 'Carla', score: 80 }
    ]);
  }, []);

  return (
    <div>
      <h2 className="text-3xl text-white font-bold mb-4">Leaderboard</h2>
      <div className="bg-white/5 rounded p-4">
        {leaders.map((l, i) => (
          <div key={i} className="flex justify-between py-2 border-b border-white/5">
            <div className="text-gray-200">#{i+1} {l.name}</div>
            <div className="text-green-300 font-semibold">{l.score}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Leaderboard;
