import React, { useEffect, useState } from 'react';

const ThreatFeed = () => {
  const [feed, setFeed] = useState([]);

  useEffect(() => {
    // Placeholder feed items; later will integrate with NewsAPI or backend
    setFeed([
      { id: 1, title: 'New credential stuffing campaign targeting cloud services', time: '2h' },
      { id: 2, title: 'Phishing kit found spoofing major banks', time: '1d' },
      { id: 3, title: 'Zero-day exploited in popular VPN', time: '3d' }
    ]);
  }, []);

  return (
    <div>
      <h2 className="text-3xl text-white font-bold mb-4">Threat Feed</h2>
      <div className="grid gap-3">
        {feed.map(item => (
          <div key={item.id} className="bg-white/5 rounded p-3">
            <div className="text-gray-200 font-semibold">{item.title}</div>
            <div className="text-gray-400 text-sm">{item.time} ago</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ThreatFeed;
