import React, { useEffect, useState, useRef } from 'react';

const AwarenessVideo = ({ script, onComplete, isVisible }) => {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [displayedText, setDisplayedText] = useState('');
  const speechRef = useRef(null);

  useEffect(() => {
    console.log('[AwarenessVideo] isVisible:', isVisible, 'script:', script);
    if (isVisible && script) {
      console.log('[AwarenessVideo] Starting video...');
      startVideo();
    } else {
      stopVideo();
    }
    return () => stopVideo();
  }, [isVisible, script]);

  const startVideo = () => {
    if (!window.speechSynthesis) {
      console.error("Web Speech API not supported");
      onComplete();
      return;
    }

    // Cancel any ongoing speech
    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(script);
    utterance.rate = 1.0;
    utterance.pitch = 1.0;
    utterance.volume = 1.0;

    // Try to select a good voice
    const voices = window.speechSynthesis.getVoices();
    const preferredVoice = voices.find(v => v.name.includes('Google US English') || v.name.includes('Microsoft Zira') || v.lang === 'en-US');
    if (preferredVoice) utterance.voice = preferredVoice;

    utterance.onstart = () => {
      console.log('[AwarenessVideo] Speech started!');
      setIsSpeaking(true);
      typeWriterEffect(script);
    };

    utterance.onend = () => {
      console.log('[AwarenessVideo] Speech ended!');
      setIsSpeaking(false);
      setTimeout(() => {
        console.log('[AwarenessVideo] Calling onComplete');
        onComplete();
      }, 2000);
    };

    utterance.onerror = (e) => {
      console.error("Speech synthesis error", e);
      setIsSpeaking(false);
      onComplete();
    };

    speechRef.current = utterance;
    // Small delay to ensure voices are loaded
    setTimeout(() => {
      console.log('[AwarenessVideo] Speaking now...');
      window.speechSynthesis.speak(utterance);
    }, 100);
  };

  const stopVideo = () => {
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    setIsSpeaking(false);
    setDisplayedText('');
  };

  const typeWriterEffect = (text) => {
    let i = 0;
    setDisplayedText('');
    const interval = setInterval(() => {
      setDisplayedText((prev) => prev + text.charAt(i));
      i++;
      if (i >= text.length) clearInterval(interval);
    }, 50);
  };

  // ALWAYS show if we have a script, regardless of isVisible
  const shouldShow = isVisible || isSpeaking || displayedText.length > 0;

  console.log('[AwarenessVideo] shouldShow:', shouldShow, 'isVisible:', isVisible, 'isSpeaking:', isSpeaking, 'displayedText length:', displayedText.length);

  if (!shouldShow) {
    console.log('[AwarenessVideo] Not showing - returning null');
    return null;
  }

  console.log('[AwarenessVideo] RENDERING COMPONENT NOW!');

  return (
    <div
      className="fixed inset-0 flex flex-col items-center justify-center p-4"
      style={{
        backgroundColor: 'rgba(0, 0, 0, 0.95)',
        zIndex: 9999,
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0
      }}
    >
      {/* AI Avatar Animation */}
      <div className="relative mb-8">
        <div
          className={`w-40 h-40 rounded-full border-4 flex items-center justify-center ${isSpeaking ? 'animate-pulse' : ''}`}
          style={{
            borderColor: '#3b82f6',
            boxShadow: '0 0 50px rgba(59, 130, 246, 0.8)'
          }}
        >
          <div
            className="w-32 h-32 rounded-full flex items-center justify-center relative overflow-hidden"
            style={{
              background: 'linear-gradient(135deg, #22d3ee 0%, #3b82f6 100%)'
            }}
          >
            {/* Animated Eye */}
            <div
              className="bg-white rounded-full transition-all duration-200"
              style={{
                width: '64px',
                height: isSpeaking ? '32px' : '4px',
                boxShadow: '0 0 20px white'
              }}
            ></div>
          </div>
        </div>

        {/* Sound waves */}
        {isSpeaking && (
          <>
            <div
              className="absolute inset-0 rounded-full border-2 animate-ping"
              style={{
                borderColor: '#22d3ee',
                opacity: 0.3
              }}
            ></div>
            <div
              className="absolute rounded-full border-2 animate-ping"
              style={{
                borderColor: '#22d3ee',
                opacity: 0.15,
                top: '-20px',
                left: '-20px',
                right: '-20px',
                bottom: '-20px'
              }}
            ></div>
          </>
        )}
      </div>

      {/* Title */}
      <h2 className="text-2xl font-bold text-cyan-400 mb-4">
        ü§ñ AI Security Awareness
      </h2>

      {/* Captions */}
      <div
        className="max-w-3xl w-full p-8 rounded-2xl border-2 min-h-[200px] flex items-center justify-center"
        style={{
          backgroundColor: 'rgba(17, 24, 39, 0.9)',
          borderColor: '#374151'
        }}
      >
        <p className="text-2xl text-cyan-300 font-mono text-center leading-relaxed">
          {displayedText}
          <span className="animate-pulse">|</span>
        </p>
      </div>

      {/* Progress indicator */}
      {isSpeaking && (
        <div className="mt-6 flex items-center gap-2">
          <div className="w-2 h-2 bg-cyan-400 rounded-full animate-bounce"></div>
          <div className="w-2 h-2 bg-cyan-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
          <div className="w-2 h-2 bg-cyan-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
        </div>
      )}

      <button
        onClick={() => { stopVideo(); onComplete(); }}
        className="mt-8 px-8 py-3 rounded-full text-base font-semibold transition-all hover:scale-105"
        style={{
          backgroundColor: '#374151',
          color: '#9ca3af',
          border: '1px solid #4b5563'
        }}
      >
        Skip Video ‚è≠Ô∏è
      </button>
    </div>
  );
};

export default AwarenessVideo;
