import React, { useState, useEffect } from 'react';
import QuizCard from '../components/QuizCard';
import ResultModal from '../components/ResultModal';

const QuizPage = ({ user }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showResult, setShowResult] = useState(false);
  const [quizResult, setQuizResult] = useState(null);

  useEffect(() => {
    fetchQuestions();
  }, []);

  const fetchQuestions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/get_quiz_questions');
      const data = await response.json();
      setQuestions(data);
    } catch (error) {
      console.error('Error fetching questions:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAnswer = (answer) => {
    const newAnswers = [...answers, answer];
    setAnswers(newAnswers);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      submitQuiz(newAnswers);
    }
  };

  const submitQuiz = async (finalAnswers) => {
    try {
      const response = await fetch('http://localhost:5000/api/submit_quiz', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: user?.id || 1,
          answers: finalAnswers,
          questions: questions
        }),
      });
      
      const data = await response.json();
      
      setQuizResult({
        score: data.score,
        correctAnswers: data.correct_answers,
        totalQuestions: data.total_questions,
        newTotalScore: data.new_total_score
      });
      
      setShowResult(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  const handleCloseResult = () => {
    setShowResult(false);
    setQuizResult(null);
    // Reset quiz
    setCurrentQuestion(0);
    setAnswers([]);
    fetchQuestions();
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setAnswers([]);
    setShowResult(false);
    setQuizResult(null);
    fetchQuestions();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400 mx-auto mb-4"></div>
          <p className="text-white">Loading quiz questions...</p>
        </div>
      </div>
    );
  }

  if (!questions.length) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="glassmorphism rounded-xl p-8 text-center">
          <div className="text-red-400 text-4xl mb-4">⚠️</div>
          <h2 className="text-xl font-semibold text-white mb-2">Error Loading Quiz</h2>
          <p className="text-gray-300 mb-4">Unable to load quiz questions. Please try again.</p>
          <button
            onClick={fetchQuestions}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold transition-all duration-200"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  if (showResult) {
    return (
      <ResultModal
        isOpen={showResult}
        onClose={handleCloseResult}
        result={quizResult}
        type="quiz"
      />
    );
  }

  return (
    <div className="min-h-screen">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-white mb-4 font-cyber">
          Cybersecurity Awareness Quiz
        </h1>
        <p className="text-gray-300 max-w-2xl mx-auto">
          Test your knowledge of cybersecurity best practices. 
          Answer all questions to get your awareness score.
        </p>
      </div>

      {/* Progress Bar */}
      <div className="mb-8">
        <div className="glassmorphism rounded-xl p-4 max-w-2xl mx-auto">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm text-gray-300">Progress</span>
            <span className="text-sm text-gray-300">
              {currentQuestion + 1} of {questions.length}
            </span>
          </div>
          <div className="w-full bg-gray-700 rounded-full h-2">
            <div 
              className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300"
              style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
            ></div>
          </div>
        </div>
      </div>

      <QuizCard
        question={questions[currentQuestion]}
        onAnswer={handleAnswer}
        questionNumber={currentQuestion + 1}
        totalQuestions={questions.length}
      />

      <div className="mt-8 text-center">
        <button
          onClick={restartQuiz}
          className="px-6 py-3 bg-gray-600 hover:bg-gray-700 text-white rounded-lg font-semibold transition-all duration-200"
        >
          Restart Quiz
        </button>
      </div>
    </div>
  );
};

export default QuizPage;
